scene.onHitWall(SpriteKind.Player, function (sprite, location) {
    if (tiles.tileAtLocationEquals(location, assets.tile`myTile6`)) {
        playerSprite.sayText("fumo", 2000, false)
    }
})
controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    up = true
    animation.stopAnimation(animation.AnimationTypes.All, playerSprite)
    if (horizontal == "left") {
        playerSprite.setImage(assets.image`characterleft2`)
    } else if (horizontal == "right") {
        playerSprite.setImage(assets.image`characterright1`)
    }
})
function block_off () {
    for (let value10 of tiles.getTilesByType(assets.tile`myTile55`)) {
        tiles.setTileAt(value10, assets.tile`myTile54`)
        tiles.setWallAt(value10, false)
    }
    for (let value11 of tiles.getTilesByType(assets.tile`myTile56`)) {
        tiles.setTileAt(value11, assets.tile`myTile57`)
        tiles.setWallAt(value11, true)
    }
    for (let value12 of tiles.getTilesByType(assets.tile`myTile51`)) {
        tiles.setTileAt(value12, assets.tile`myTile53`)
    }
}
