function dev_text () {
    if (twicetime == true) {
        // wow speed
        controller.moveSprite(playerSprite, 0, 0)
        timer.after(200, function () {
            playerSprite.sayText("What are you?", 2000, false)
            timer.after(2000, function () {
                dev.sayText("I'm one of the developers", 2000, false)
                timer.after(2000, function () {
                    dev.sayText("I've come to tell you that we ran out of budget ", 3000, false)
                    timer.after(3000, function () {
                        dev.sayText("So here is a room with all the stuff we didn't use yet", 3000, false)
                        // wow speed
                        controller.moveSprite(playerSprite, 120, 0)
                        timer.after(500, function () {
                            twicetime = false
                        })
                    })
                })
            })
        })
    }
}
scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile22`, function (sprite, location) {
    // wow speed
    controller.moveSprite(playerSprite, 0, 0)
    scene.cameraShake(4, 500)
    windspeed = 4
    timer.after(200, function () {
        for (let value122 of tiles.getTilesByType(assets.tile`myTile22`)) {
            tiles.setTileAt(value122, assets.tile`myTile74`)
            tiles.setWallAt(value122, true)
        }
        windspeed = 0
        // wow speed
        controller.moveSprite(playerSprite, 120, 0)
    })
    timer.after(300, function () {
        if (first_time == true) {
            playerSprite.sayText("OH WHAT? That can't be good!", 2000, false)
            first_time = false
        }
        timer.after(300, function () {
            summon_water()
        })
    })
})
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (cyoteTimer > 0 && canJump == true) {
        jumping(playerSprite)
    }
})
function clear_level () {
    up = false
    for (let value32 of sprites.allOfKind(SpriteKind.fumo)) {
        value32.destroy()
    }
    for (let value33 of sprites.allOfKind(SpriteKind.hazard)) {
        value33.destroy()
    }
}
function place_developer () {
    for (let fumoValue of tiles.getTilesByType(assets.tile`myTile94`)) {
        dev = sprites.create(assets.image`myImage1`, SpriteKind.developer)
        tiles.placeOnTile(dev, fumoValue)
        tiles.setWallAt(fumoValue, true)
    }
}
